(()=>{"use strict";(e=>{const t=document.querySelector("#timer-hours"),o=document.querySelector("#timer-minutes"),r=document.querySelector("#timer-seconds"),n=setInterval((()=>{let e=(new Date("23 december 2021").getTime()-(new Date).getTime())/1e3,c=Math.floor(e/60/60),l=Math.floor(e/60%60),m=Math.floor(e%60);e>0?(t.textContent=c<10?"0"+c:c,o.textContent=l<10?"0"+l:l,r.textContent=m<10?"0"+m:m):clearInterval(n)}),1e3)})()})();